query listAccounts ($after: String, $before: String, $first: Float, $last: Float, $name: String) {
    listAccounts (after: $after, before: $before, first: $first, last: $last, name: $name) {
        edges {
            cursor
            node {
                _id
                amount
                createdAt
                number
                owner {
                    _id
                    company {
                        _id
                        createdAt
                        name
                    }
                    createdAt
                    email
                    name
                    password
                    role
                }
            }
        }
        pageInfo {
            endCursor
            hasNextPage
            hasPreviousPage
            startCursor
        }
        totalCount
    }
}

